import "./ExpenseForm.css";
import React, { useState } from "react";

function ExpenseForm() {
  const [orgTitle, setTitle] = useState("");
  const [orgDate, setDate] = useState("");
  const [orgAmount, setAmount] = useState("");

  //   const [userInput, setUserInput] = useState({
  //     enteredTitle: "",
  //     enteredAmount: "",
  //     enteredDate: "",
  //   });

  const TitleHandler = (event) => {
    setTitle(event.target.value);
    // setUserInput({
    //   ...userInput,
    //   enteredTitle: event.target.value,
    // });
  };

  const DateHandler = (event) => {
    setDate(event.target.value);
    // setUserInput({
    //   ...userInput,
    //   enteredDate: event.target.value,
    // });
  };

  const AmountHandler = (event) => {
    setAmount(event.target.value);
    // setUserInput({
    //   ...userInput,
    //   enteredAmount: event.target.value,
    // });
  };

  const formHandler = (e) => {
    e.preventDefault();

    console.log("clicked");

    const expenseData = {
      Title: orgTitle,
      ammout: orgAmount,
      date: new Date(orgDate),
    };

    console.log(expenseData);
  };
  return (
    <form onClick={formHandler}>
      <div className="new-expense__controls">
        <label className="new-expense__control">Title</label>
        <input type="text" onChange={TitleHandler}></input>
      </div>
      <div className="new-expense__controls">
        <label className="new-expense__control">Amount</label>
        <input
          type="number"
          min="0.01"
          step="0.01"
          onChange={AmountHandler}
        ></input>
      </div>
      <div className="new-expense__controls">
        <label className="new-expense__control">Date</label>
        <input
          type="date"
          min="2019-01-01"
          max="2022-12-31"
          onChange={DateHandler}
        ></input>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
}

export default ExpenseForm;
